mapper.select:
SELECT r.id, r.title, r.content, r.recruit_type, r.status, r.image_url AS imageUrl, DATE_FORMAT(r.started_at, '%Y-%m-%d') AS startedAt, DATE_FORMAT(r.ended_at, '%Y-%m-%d') AS endedAt, DATE_FORMAT(r.created_at, '%Y-%m-%d') AS createdAt, DATE_FORMAT(r.updated_at, '%Y-%m-%d') AS updatedAt, DATE_FORMAT(r.deleted_at, '%Y-%m-%d') AS deletedAt, r.member_id AS memberId, m.name AS memberName, d.name AS departmentName, r.recruitment_template_id AS recruitmentTemplateId, r.introduce_template_id AS introduceTemplateId, r.recruitment_request_id AS recruitmentRequestId FROM recruitment r JOIN member m ON r.member_id = m.id LEFT JOIN department d ON m.department_id = d.id WHERE r.deleted_at IS NULL
AND r.title LIKE CONCAT('%', #{condition.keyword}, '%')
AND r.recruit_type = #{condition.recruitType}
AND d.name = #{condition.departmentName}
AND r.status = #{condition.status}
AND r.started_at >= #{condition.startFrom}
AND r.ended_at <= #{condition.endTo}
ORDER BY r.created_at DESC
